CREATE TABLE "public"."reviews" ("id" serial NOT NULL, "menuId" uuid NOT NULL, "userId" text NOT NULL, "rating" integer NOT NULL, "comment" text NOT NULL, "createdAt" timestamptz NOT NULL DEFAULT now(), PRIMARY KEY ("id") , FOREIGN KEY ("menuId") REFERENCES "public"."menu"("id") ON UPDATE cascade ON DELETE cascade, FOREIGN KEY ("userId") REFERENCES "public"."user"("id") ON UPDATE cascade ON DELETE cascade, UNIQUE ("menuId", "userId"));
